import { useEffect, useRef, useState } from 'react'
import './Map.css'

import { AnimatePresence, motion } from 'framer-motion'
import ListaProyectos from './proyectos.json'

export const Map = () => {

    const [activeRegion, setActiveRegion] = useState(null)
    const [nameActiveRegion, setNameActiveRegion] = useState(null)
    const handleChangeRegion = (regionInfo, regionName) => {
        setActiveRegion(regionInfo)
        setNameActiveRegion(regionName)
    }

    return (
        <div className='interactive-container'>
            <div className="map__container">
                <InteractiveMap handleChangeRegion={handleChangeRegion} nameActiveRegion={nameActiveRegion} />
            </div>
            <div>
                <h3>
                    {nameActiveRegion !== null ? `Proyectos realizados en ${nameActiveRegion}` : ''}
                </h3>
                {activeRegion !== null ?
                    <div className="region__info">
                        <RegionInfo activeRegion={activeRegion} regionName={nameActiveRegion} />
                    </div>
                    :

                    <div className="region__info">
                        <h3>Seleccione un proyecto para más información</h3>
                    </div>

                }
            </div>
        </div>

    )
}

const RegionInfo = ({ activeRegion, regionName }) => {

    const [arePda, setArePda] = useState(false)
    const [areEsp, setAreEsp] = useState(false)
    const [areCdu, setAreCdu] = useState(false)

    useEffect(() => {

        let countPDA = 0;
        let countESP = 0;
        let countCDU = 0;

        activeRegion.forEach(item => {
            if (item.tipo === 'PDA') countPDA++;
            if (item.tipo === 'ESP') countESP++;
            if (item.tipo === 'Catastro de Usuarios') countCDU++;
        })
        setArePda(countPDA > 0)
        setAreEsp(countESP > 0)
        setAreCdu(countCDU > 0)

    }, [activeRegion])

    return (
        <motion.div layout className="info__container">
            <AnimatePresence>
                {arePda &&

                    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }} className="section_info">
                        <h3>PDA - Dpto. {regionName}</h3>
                        <div className="section_content">
                            {activeRegion.map((item, index) => {

                                if (item.tipo === 'PDA')
                                    return (
                                        <Card
                                            key={index}
                                            empresa={item.empresa}
                                            fecha_fin={item.fecha_finalizacion}
                                            descripcion={item.descripcion}
                                            subscriptores={item.subscriptores}
                                        />
                                    )

                            })}
                        </div>
                    </motion.div>

                }
                {
                    areEsp &&
                    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }} className="section_info">
                        <h3>ESP - Dpto. {regionName}</h3>
                        <div className="section_content">
                            {activeRegion.map((item, index) => {

                                if (item.tipo === 'ESP')
                                    return (
                                        <Card
                                            key={index}
                                            empresa={item.empresa}
                                            fecha_fin={item.fecha_finalizacion}
                                            descripcion={item.descripcion}
                                            subscriptores={item.subscriptores}
                                        />
                                    )

                            })}
                        </div>
                    </motion.div>
                }
                {areCdu &&
                    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }} className="section_info">
                        <h3>Catastro de Usuarios - Dpto. {regionName}</h3>
                        <div className="section_content">
                            {activeRegion.map((item, index) => {

                                if (item.tipo === 'Catastro de Usuarios')
                                    return (
                                        <Card
                                            key={index}
                                            empresa={item.empresa}
                                            fecha_fin={item.fecha_finalizacion}
                                            descripcion={item.descripcion}
                                            subscriptores={item.suscriptores}
                                        />
                                    )

                            })}
                        </div>
                    </motion.div>
                }
            </AnimatePresence>
        </motion.div>
    )
}

const Card = ({ empresa, fecha_fin = null, descripcion, subscriptores = null }) => {

    return <motion.div initial={{ opacity: 0, x: -100 }} animate={{ opacity: 1, x: 0 }} className="section_item">
        <h4>{empresa}</h4>
        {fecha_fin && <caption>{fecha_fin}</caption>}
        <p>{descripcion && descripcion}</p>
        {subscriptores && <p>Suscriptores: {subscriptores}</p>}
    </motion.div>

}

