const Path = ({ id, d, fill, stroke, strokeWidth, handleChangeRegion, nameActiveRegion }) => {

    const proyectos = ListaProyectos[id] || []
    const [cantidadProyectos, setCantidadProyectos] = useState(0)

    useEffect(() => {

        setCantidadProyectos(proyectos.length);

    }, []);

    const handleClick = () => {

        handleChangeRegion(proyectos, id)
    }

    return (
        <motion.g className={'group_region'}>
            <motion.path
                className={`${cantidadProyectos > 0 ? 'has__projects' : 'nohas_projects'} ${nameActiveRegion === id ? 'active' : ''}`}
                initial={{ opacity: 0 }}
                whileInView={{ opacity: 1 }}
                viewport={{ once: true }}
                onClick={proyectos.length > 0 ? handleClick : () => console.log('No projects')}
                id={id}
                d={d}
                fill={fill}
                stroke={stroke}
                strokeWidth={strokeWidth}
            />

        </motion.g>
    )

}